import React from "react";
import './UserUI.css'
import { useState } from "react";

const UserUI=()=>{
    var imgFile
    const [data,setdata]=useState({
        ProductId:'',
        ProductName:'',
        Productprice:''

    })
    let name1,value;
    const getData=(e)=>{
        name1=e.target.name;
        value=e.target.value;
        setdata({...data,[name1]:value});
        // console.log(data);
    }
    const [files, setFiles] = useState(null)  
const filesHandler = (e) => {

    //  let reader=new FileReader()
    //  reader.onload=function(e){
    //     setFiles(reader.result)
    //  }
    //  reader.readAsArrayBuffer(e.target.file)

     console.log(e.target.files[0]);
    //let r=new FileReader();
   // r.readAsArrayBuffer(e.target.files[0]);
    //console.log(r.result);
    setFiles(e.target.files[0]);
    // imgFile=r.result
    let file=e.target.files[0]
    let reader=new FileReader();
    reader.onload=function(event)
    {
        setFiles(event.target.result);
    }
    reader.readAsArrayBuffer(file)
}
const dataSubmit=async()=>{
        const test ={
            ProductId:data.ProductId,
            ProductName:data.ProductName,
            ProductPrice:data.Productprice
        }

        console.log(JSON.stringify(test))

    await fetch('https://formdata.pk6361439.workers.dev',{
        mode:'no-cors',
        method: 'POST',
        headers: {
            'Content-Type':'*'
        },

        body: JSON.stringify(test)
    })
    .then(async(response)=>{
        await response.text().then((value)=>{
          console.log(value)
        })
        //console.log(response.text());
    })
    // console.log(data);
     console.log(files[0])
    setdata({
        ProductId:'',
        ProductName:'',
        Productprice:''
    });
    setFiles(null);
}
//let r=new FileReader();
//r.readAsArrayBuffer(files[0])

const imageSubmit=async()=>{
    // const formData = new FormData();

	// formData.append('File', files);


    await fetch(`https://image.pk6361439.workers.dev?key=${data.ProductId}`,{
    method: 'POST',
    headers: {
        'Content-Type': '*'
    },

    body:files
})
.then(async(response)=>{
    await response.text().then((value)=>{
      console.log(value)
    })

  })
}
    return(
        <>
        <form className="form">
            <div className="form-group">
            <label for="id"><h4>Enter Product Id</h4></label>
    <input type="text" className="form-control" id="id" placeholder="ex-123 or abc123" name="ProductId" value={data.ProductId} onChange={getData}/>
    <label for="ProductName"><h4>Enter Product Name</h4></label>
    <input type="text" className="form-control" id="ProductName" placeholder="ex-macbook Air" name="ProductName" value={data.ProductName} onChange={getData} />
    <label for="Price"><h4>Enter Product Price</h4></label>
    <input type="text" className="form-control" id="Price" placeholder="ex-Rs 12000" name="Productprice" value={data.Productprice} onChange={getData}/>
    <label for="image"><h4>Select Product photo</h4></label>
    <input type="file" class="form-control-file" id="image" onChange={e => filesHandler(e)}></input>
    <button type="button" class="btn btn-primary" onClick={dataSubmit}>Primary</button>
    <button type="button" class="btn btn-primary" onClick={imageSubmit}>Upload</button>
  </div>

        </form>
        </>
    )
}
export default UserUI
